import { Data, animate, Override, Animatable } from "framer";

const data = Data({ toggled: false });

// Invert the value of toggled (turn if off if it's on, or on if it's off)
export const Toggle: Override = () => {
	return {
		onTap() {
			data.toggled = !data.toggled;
		}
	};
};

// if toggled, override the value of text_string,
// otherwise leave it as is in the design view
export const TextOverride: Override = () => {
	return {
		TextContent: data.toggled && "Whaddup"
	};
};
